package findthedifference

import "testing"

func Test_find(t *testing.T) {
	tests := map[string]struct {
		s    string
		t    string
		want byte
	}{
		"1": {
			"",
			"",
			't',
		},
	}

	for name, tc := range tests {
		t.Run(name, func(t *testing.T) {
			got := findTheDifference(tc.s, tc.t)

			if got != tc.want {
				t.Errorf("got %c instead of %c", got, tc.want)
			}
		})
	}
}
